mapper.select:
SELECT s.id, s.name, s.no, s.gender, s.phone, s.id_card as idCard, s.is_college as isCollege, s.address, s.degree, s.graduation_date as graduationDate, s.clazz_id as clazzId, s.violation_count as violationCount, s.violation_score as violationScore, s.create_time as createTime, s.update_time as updateTime, c.name as clazzName FROM student s LEFT JOIN clazz c ON s.clazz_id = c.id
AND s.name LIKE CONCAT('%', #{name}, '%')
AND s.degree = #{degree}
AND s.clazz_id = #{clazzId}
ORDER BY s.update_time DESC

SELECT COUNT(*)
FROM student s


        AND s.name LIKE CONCAT('%', #{name}, '%')


        AND s.degree = #{degree}


        AND s.clazz_id = #{clazzId}

mapper.insert:
INSERT INTO student (
    name, no, gender, phone, id_card, is_college, address, 
    degree, graduation_date, clazz_id, violation_count, violation_score,
    create_time, update_time
) VALUES (
    #{name}, #{no}, #{gender}, #{phone}, #{idCard}, #{isCollege}, #{address},
    #{degree}, #{graduationDate}, #{clazzId}, #{violationCount}, #{violationScore},
    #{createTime}, #{updateTime}
)
